import { defineStore } from 'pinia'

export const useWindowStore = defineStore('window', {
  state: () => {
    return window.store || {
      apps: [],
      locales: {},
    }
  },
  // could also be defined as
  // state: () => ({ count: 0 })
  actions: {
    addApp(app) {
      this.apps.push(app)
    },
    deleteApp(key) {
      this.apps.splice(key, 1)
    },
  },
})
